mapper.select:
select *
from attraction
where att_lat between #{swlat} and #{nelat}
and att_lng between #{swlng} and #{nelng}


select count(*) cnt
from att_review
where att_no = #{no}


select nvl(round(avg(review_star),1), 0)
from att_review
where att_no = #{no}


select *
from(
    select * from attraction
    order by DBMS_RANDOM.RANDOM
	)
where rownum<4
and att_no != #{no}
and att_place = #{place}

select user_no, count(user_no)
from att_review
where user_no = #{userNo}
and att_no = #{attNo}
group by user_no


        select restaurant_no no, restaurant_name as name, restaurant_picture as picture, restaurant_address as address
		from(
		    select * from restaurant
		    order by DBMS_RANDOM.RANDOM
			)
		where rownum<5
